tinymce.PluginManager.add('youtube', function (editor, url) {
    // Add a button that opens a window
    editor.addButton('youtube', {
        text: 'Youtube',
        icon: false,
        onclick: function () {
            editor.windowManager.open({

                title: 'Bạn hãy chọn 1 video',
                width: 900,
                height: 655,
                url: url + '/youtube.aspx'
            })
        }
    });
});